  <div class="post-it-container" id="post-it-card">
    <div class="cat-kaomoji">ฅ(^・ω・^)ฅ Nhấn để chọn</div>
    <!-- HTML 修改处 -->
    <button id="theme-toggle" class="theme-icon" aria-label="切换主题" title="切换主题">
      <span class="icon-sun">☀️</span>
      <span class="icon-moon">🌙</span>
    </button>
    <div class="post-it-collapsible" id="collapsible-content">
      <div class="content-wrapper">
        <div id="options-container"></div>
      </div>
